<script>
function option(el){
	var div = document.querySelector("[name='" + el.id + "']");
	var view = document.querySelector(".view");
	document.querySelector("[name='result']").innerHTML = "";
	if (div.classList.contains("display-none")) {
		view.classList.add("opacity-0");
		div.classList.remove("display-none");    
		setTimeout(function() {			
			view.classList.remove("view");
			view.classList.add("display-none");
			div.classList.remove("opacity-0");			
			div.classList.add("view");
		}, 200);
	}
}

function selectIndex(index){
	// Adiciona a mensagem "Carregando" na div "result"
	$("[name='result']").append('<div class="loader-ball-3"><div></div><div></div><div></div></div>');	
	// Faz a solicitação AJAX para o arquivo PHP
	$.ajax({
		url: 'https://app.workz.com.br/env/<?= $env ?>/backengine/wa0013/estatisticas/async/statistics.php',
		method: "GET",
		data: { index: index },
		success: function(response){
			$("[name='result']").html('');
			// Atualiza a div "result" com o conteúdo retornado pelo arquivo PHP
			$("[name='result']").append('<span id="opt1" onclick="option(this)" class="fa-stack pointer fs-f"><i class="fas fa-circle fa-stack-2x light-gray"></i><i class="fas fa-angle-left fa-stack-2x" style="color: <? echo $colours[0]; ?>"></i></span>');
			$("[name='result']").append(response);
		},
		error: function(){
			// Caso ocorra algum erro na solicitação, exiba uma mensagem de erro
			console.error('Erro na solicitação AJAX.');
		}
	});
}

function clipboardCopy(id){
	var copyText = document.getElementById(id);
	copyText.select();
	copyText.setSelectionRange(0, 99999);
	document.execCommand("copy");
	//alert("Copiado para área de transferência");
	sAlert('', '', 'Copiado para área de transferência', '');
}


function copyToClipboard(val){
	// Defina o valor que você deseja copiar
	var textoParaCopiar = val;

	// Tenta copiar para a área de transferência
	navigator.clipboard.writeText(textoParaCopiar)
		.then(function() {			
			sAlert('', '', 'Copiado para área de transferência.', '');
		})
		.catch(function(error) {			
			sAlert('', '', 'Erro ao copiar para área de transferência.', '');
		});	
}

//TABLE TO EXCEL

var tableToExcel = (function(){
	var uri = 'data:application/vnd.ms-excel;base64,'
	, template = '<html xmlns:o="urn:schemas-microsoft-com:office:office" xmlns:x="urn:schemas-microsoft-com:office:excel" xmlns="http://www.w3.org/TR/REC-html40"><head><!--[if gte mso 9]><xml><x:ExcelWorkbook><x:ExcelWorksheets><x:ExcelWorksheet><x:Name>{worksheet}</x:Name><x:WorksheetOptions><x:DisplayGridlines/></x:WorksheetOptions></x:ExcelWorksheet></x:ExcelWorksheets></x:ExcelWorkbook></xml><![endif]--></head><body><table>{table}</table></body></html>'
	, base64 = function(s) { return window.btoa(unescape(encodeURIComponent(s))) }
	, format = function(s, c) { return s.replace(/{(\w+)}/g, function(m, p) { return c[p]; }) }
	return function(table, name) {
		if (!table.nodeType) table = document.getElementById(table)
		var ctx = {worksheet: name || 'Worksheet', table: table.innerHTML}
		window.location.href = uri + base64(format(template, ctx))
	}
})();
</script>