//LOAD USER DATA
//IF WORKZ SESSION DO NOT EXISTS
if(isset($_SESSION['wz'])){
	//FOLLOWED USERS BY USER	
	$followed = array_column(search('hnw', 'usg', 's1', "s0 = '".$_SESSION['wz']."'"), 's1');
	
	//COMPANIES
	$companies = array_column(search('cmp', 'employees', 'em', "us = '".$_SESSION['wz']."' AND nv > 0"), 'em');
	$blocked_companies = array_column(search('cmp', 'companies', 'id', "id IN (".implode(',', $companies).") AND pg = 0"), 'id');	
	$companies = array_values(array_diff($companies, $blocked_companies));
	
	//TEAMS
	$teams = search('cmp', 'teams_users', 'cm,st', "us = '".$_SESSION['wz']."'");				
	foreach($teams as $key => $team){				
		$cm = $team['cm'];
		$st = $team['st'];
		$team = search('cmp', 'teams', 'pg,em', "id = '".$cm."'");
		if($cm == 0 || $team[0]['pg'] == 0 || !in_array($team[0]['em'], $companies) || $st == 0){
			unset($teams[$key]);
		}
	}
	$teams = array_values(array_unique(array_column($teams, 'cm')));
	
	require($_SERVER['DOCUMENT_ROOT'].'/functions/getAddress.php');
	
	/*?>
	<iframe id="audio-frame" src="/audio_player.php" style="display: none;"></iframe>
	<?php*/
	$loggedUser = search('hnw', 'hus', '', "id = '{$_SESSION['wz']}'")[0];
	if(empty($loggedUser['im'])){ $loggedUser['im'] =  ''; }
	if(empty($loggedUser['tt'])){ $loggedUser['tt'] = $loggedUser['ml']; }	
}else{	
	session_destroy();
}
